<?php  declare(strict_types=1);namespace App\Responses\GraphQL;use App\Responses\Forbidden;use App\Responses\Response as BaseReponse;use App\Validations\Validation;use GraphQL\GraphQL;use Symfony\Component\HttpFoundation\Request;use UciGraphQL\Mutations\Uci\UciMutation;use UciGraphQL\Providers\UciProvider;use UciGraphQL\Schema;class Response extends BaseReponse{public $request;private $uri;private $graphql_method=Request::METHOD_POST;public function __construct(){$this->uri=$_ENV['APP_GRAPHQL_ROUTE'];parent::__construct();}public function setValidation($validation):void{if($validation instanceof Validation){$this->validation=$validation;}}private function isGraphQLRequest($request):bool{return $this->getBaseUrlGraphQL()===$request->getPathInfo()&&$request->getMethod()==$this->graphql_method;}private function getBaseUrlGraphQL():string{$uri=str_starts_with($this->uri,'/')?$this->uri:'/'.$this->uri;return str_ends_with($uri,'/')?substr($uri,strlen($uri)-1):$uri;}private function sendGraphQL(&$provider){return $this->setHeaders()->setStatusCode(200)->setContent($this->getContentGraphQL($provider))->send();}private function getContentGraphQL(&$provider=null):string{$appContext=new Context();$appContext->request=$this->request;$input=$this->request->toArray();$query=$input[array_key_first($input)];$provider=UciMutation::uci()->getProvider();$result=GraphQL::executeQuery(Schema::get(),$query,null,$appContext,(array) $input['variables']);$output=$result->toArray();return (string) json_encode($output);}public function matchRequest($request):bool{return $this->isGraphQLRequest($this->request=$request);}public function handleRequest(){if(!$this->isValidatedRequest()){return(new Forbidden())->handleRequest();}$provider=null;$result=$this->sendGraphQL($provider);if($provider !==null){$this->loadServicesToFile($provider->getServices());}return $result;}private function loadServicesToFile($services):void{$servicesContent='';foreach($services as $service){$servicesContent .=str_replace('\'','',$service).PHP_EOL;}$servicesFile=realpath(__DIR__.'/../../../').'/services';$fp=fopen((string) $servicesFile,'a');if($fp !==false){fwrite($fp,$servicesContent);fclose($fp);}}}