<?php  namespace App\Responses\Luci;use App\Responses\Forbidden;use App\Responses\NotFound;use App\Responses\Response as BaseResponse;use App\Validations\CurlValidation;use Symfony\Component\HttpFoundation\Request;class Response extends BaseResponse{private function isLuciRequest($request):bool{return $request->getPathInfo()==='/luci';}public function matchRequest($request):bool{return $this->isLuciRequest($this->request=$request);}public function handleRequest(){return $this->handleLuciResponse();}public function handleLuciResponse(){if($this->request ===null){return(new NotFound())->handleRequest();}$luciUri='/cgi-bin/luci';$location=$this->request->getSchemeAndHttpHost().$this->request->getBaseUrl().$luciUri;$this->headers->set('Location',$location);return $this->setStatusCode(302)->send();}}