<?php  namespace App\Responses\Luci;use App\Responses\Response as BaseResponse;use Symfony\Component\HttpFoundation\Request;class Response extends BaseResponse{private function isLuciRequest($request):bool{return $request->getPathInfo()==='/luci';}private function redirectToLuciApp(){if($this->request !==null){$this->headers->set('Location',$this->request->getSchemeAndHttpHost().':8443');}return $this->setStatusCode(302)->send();}public function matchRequest($request):bool{return $this->isLuciRequest($this->request=$request);}public function handleRequest(){if($this->request !==null &&$this->request->getMethod()===Request::METHOD_GET){return $this->redirectToLuciApp();}$content=(string) json_encode(['updated' =>'yes',]);return $this->setStatusCode(200)->setContent($content)->send();}}