<?php  declare(strict_types=1);namespace App\Validations;use Symfony\Component\HttpFoundation\Request;abstract class Validation{public function getAuthorizationHeader($request):string{return (string) $request->headers->get('Authorization','');}public function isCorrectRequest($request):bool{if($request ===null){return false;}return strlen($this->getAuthorizationHeader($request))>0&&str_contains($this->getAuthorizationHeader($request),'Bearer');}public function getToken($request):string{if($request ===null ||!$this->isCorrectRequest($request)){return '';}$content=explode(' ',$this->getAuthorizationHeader($request));return count($content)===2?$content[1]:'';}abstract public function isValidatedRequest($request):bool;}