<?php  declare(strict_types=1);namespace UciGraphQL\Types;trait UciForbidden{protected $forbiddenConfigurations=null;protected function isCorrectForbiddenConfigurations():bool{return $this->forbiddenConfigurations !==null &&is_array($this->forbiddenConfigurations);}protected function isCorrectConfigForbiddenConfigurations($configName):bool{return $this->isCorrectForbiddenConfigurations()&&isset($this->forbiddenConfigurations[$configName])&&is_array($this->forbiddenConfigurations[$configName]);}protected function isCorrectSectionForbiddenConfigurations($configName,$sectionName):bool{return $this->isCorrectConfigForbiddenConfigurations($configName)&&isset($this->forbiddenConfigurations[$configName][$sectionName])&&is_array($this->forbiddenConfigurations[$configName][$sectionName]);}protected function getConfigsForbidden():array{if(!$this->isCorrectForbiddenConfigurations()){return[];}$configsForbidden=[];if(is_iterable($this->forbiddenConfigurations)){foreach($this->forbiddenConfigurations as $configName =>$content){if($content ===true){array_push($configsForbidden,$configName);}}}return $configsForbidden;}protected function getSectionsForbidden($configName):array{if(!$this->isCorrectConfigForbiddenConfigurations($configName)){return[];}$sectionsForbidden=[];if(isset($this->forbiddenConfigurations[$configName])&&is_iterable($this->forbiddenConfigurations[$configName])){foreach($this->forbiddenConfigurations[$configName]as $sectionName =>$content){if($content ===true){array_push($sectionsForbidden,$sectionName);}}}return $sectionsForbidden;}protected function getOptionsForbidden($configName,$sectionName):array{if(!$this->isCorrectSectionForbiddenConfigurations($configName,$sectionName)){return[];}return isset($this->forbiddenConfigurations[$configName][$sectionName])?$this->forbiddenConfigurations[$configName][$sectionName]:[];}}