<?php  namespace Symfony\Component\HttpFoundation\Session\Storage\Handler;use Symfony\Component\Cache\Marshaller\MarshallerInterface;class MarshallingSessionHandler implements \SessionHandlerInterface,\SessionUpdateTimestampHandlerInterface{private $handler;private $marshaller;public function __construct(AbstractSessionHandler $handler,MarshallerInterface $marshaller){$this->handler=$handler;$this->marshaller=$marshaller;}public function open($savePath,$name){return $this->handler->open($savePath,$name);}public function close(){return $this->handler->close();}public function destroy($sessionId){return $this->handler->destroy($sessionId);}public function gc($maxlifetime){return $this->handler->gc($maxlifetime);}public function read($sessionId){return $this->marshaller->unmarshall($this->handler->read($sessionId));}public function write($sessionId,$data){$failed=[];$marshalledData=$this->marshaller->marshall(['data' =>$data],$failed);if(isset($failed['data'])){return false;}return $this->handler->write($sessionId,$marshalledData['data']);}public function validateId($sessionId){return $this->handler->validateId($sessionId);}public function updateTimestamp($sessionId,$data){return $this->handler->updateTimestamp($sessionId,$data);}}