<?php  declare(strict_types=1);namespace GraphQL\Executor;use GraphQL\Error\Error;use GraphQL\Executor\Promise\PromiseAdapter;use GraphQL\Language\AST\FragmentDefinitionNode;use GraphQL\Language\AST\OperationDefinitionNode;use GraphQL\Type\Schema;class ExecutionContext{public $schema;public $fragments;public $rootValue;public $contextValue;public $operation;public $variableValues;public $fieldResolver;public $errors;public $promises;public function __construct($schema,$fragments,$root,$contextValue,$operation,$variables,$errors,$fieldResolver,$promiseAdapter){$this->schema=$schema;$this->fragments=$fragments;$this->rootValue=$root;$this->contextValue=$contextValue;$this->operation=$operation;$this->variableValues=$variables;$this->errors=$errors?:[];$this->fieldResolver=$fieldResolver;$this->promises=$promiseAdapter;}public function addError(Error $error){$this->errors[]=$error;return $this;}}