<?php  declare(strict_types=1);namespace GraphQL\Language;class Token{const SOF='<SOF>';const EOF='<EOF>';const BANG='!';const DOLLAR='$';const AMP='&';const PAREN_L='(';const PAREN_R=')';const SPREAD='...';const COLON=':';const EQUALS='=';const AT='@';const BRACKET_L='[';const BRACKET_R=']';const BRACE_L='{';const PIPE='|';const BRACE_R='}';const NAME='Name';const INT='Int';const FLOAT='Float';const STRING='String';const BLOCK_STRING='BlockString';const COMMENT='Comment';public $kind;public $start;public $end;public $line;public $column;public $value;public $prev;public $next;public function __construct($kind,$start,$end,$line,$column,?Token $previous=null,$value=null){$this->kind=$kind;$this->start=$start;$this->end=$end;$this->line=$line;$this->column=$column;$this->prev=$previous;$this->next=null;$this->value=$value;}public function getDescription(){return $this->kind.($this->value?' "'.$this->value.'"':'');}public function toArray(){return['kind' =>$this->kind,'value' =>$this->value,'line' =>$this->line,'column' =>$this->column,];}}