<?php  declare(strict_types=1);namespace GraphQL\Type\Definition;use Exception;use GraphQL\Error\Error;use GraphQL\Language\AST\BooleanValueNode;use GraphQL\Language\AST\Node;use GraphQL\Utils\Utils;use function is_bool;class BooleanType extends ScalarType{public $name=Type::BOOLEAN;public $description='The `Boolean` scalar type represents `true` or `false`.';public function serialize($value){return (bool) $value;}public function parseValue($value){if(is_bool($value)){return $value;}throw new Error('Cannot represent value as boolean: '.Utils::printSafe($value));}public function parseLiteral($valueNode,?array $variables=null){if(!$valueNode instanceof BooleanValueNode){throw new Exception();}return $valueNode->value;}}